import React, {useState} from 'react';
import Service from "../../services/api-service";
import Emoji from "../emoji/emoji";

let initialState = {
    name: '',
    category: '',
    price: undefined,
};
const ApiService = new Service()

const ItemAddForm = () => {

    let [newBike, setNewBike] = useState(initialState)


    const handleChange = (event) => {
        event.persist();
        setNewBike(prevValue => ({ ...prevValue, [event.target.name]: event.target.value }));
    };

    const onSubmit = (e) => {
        e.preventDefault();
        ApiService.postBike(newBike);
    };
        return (
            <div>
                <h3>  <Emoji symbol={''} />Create new rent </h3>
                <form
                    className="bottom-panel d-flex"
                    onSubmit={onSubmit}>

                    <input type="text"
                           className="form-control "
                           value={newBike.name}
                           onChange={handleChange}
                           placeholder="Name" />
                    <input type="text"
                           className="form-control "
                           value={newBike.category}
                           onChange={handleChange}
                           placeholder="Type" />
                    <input type="text"
                           className="form-control "
                           value={newBike.price}
                           onChange={handleChange}
                           placeholder="Price" />


                    <button type="submit"
                            className="add-btn-form">Add</button>
                </form>
            </div>

        );
}

export default ItemAddForm
