export default class ApiService {

    _apiBase = 'http://localhost:5000';


    getResource = async (url) => {
        const res = await fetch(`${this._apiBase}${url}`);

        if (!res.ok) {
            throw new Error(`Could not fetch ${url}` +
                `, received ${res.status}`)
        }
        return await res.json();
    };

    postData = async (url, data) => {
        const res = await fetch(`${this._apiBase}${url}`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Access-Control-Allow-Origin': '*'
            },
            body: JSON.stringify(data) // body data type must match "Content-Type" header
        });
        if (!res.ok) {
            throw new Error(`Could not fetch ${url}` +
                `, received ${res.status}`)
        }
        return await res.json(); // parses JSON response into native JavaScript objects
    }

    getAllBikes = async () => {
        const res = await this.getResource(`/bike/`);
        return res.results
    };

    getBike = async (id) => {
        const person = await this.getResource(`/bike/${id}/`);
        return this._transformPerson(person);
    };

    postBike = async (data) => {
        const res = await this.postData(`/bike/`, data);
        return res.results
    };
    deleteBike = async (data) => {
        const res = await this.postData(`/bike/`, data);
        return res.results
    };


};
