import React, {useState, useEffect } from 'react';

import AvailableList from '../available-list/avaliable-list';
import ItemAddForm from "../form/form";
import Service from "../../services/api-service";


 const ApiService = new Service()

const App = () => {
    const todoData = [
        { id: 1, name: 'Drink Coffee', available: false, category: 'road', price: 1 },
        { id: 2, name: 'Make Awesome App', available: true, category: 'mountain', price: 2 },
        { id: 3, name: 'Have a lunch', available: false, category: 'speed', price: 3 }
    ];
    let [appState, setAppState] = useState([])
    let [isLoading, setIsLoading] = useState(false)

    const updateApp = async () => {
        const allBikes = await ApiService.getAllBikes()
        setAppState(allBikes)
    }

    return (
        <div className="p-3 mb-2 bg-light text-dark">
            <button onClick={updateApp}
            >Update </button>
            <h1> Awesome Bike Rental</h1>

            <div className="top-panel d-flex">
                <ItemAddForm />
            </div>

            <AvailableList bikes={todoData} />
        </div>
    );
};

export default App;
